require 'cloudbox'
require 'simplecov'
SimpleCov.start
RSpec.configure do |config|
  config.treat_symbols_as_metadata_keys_with_true_values = true
  config.run_all_when_everything_filtered = true
  config.filter_run :focus
end

RSpec.configure do |config|
  config.before(:each) do
    Cloudbox::Manager.stub(:execute).with("VBoxManage", "list", "vms").and_return(mock_vms_list_output)
    Cloudbox::Manager.stub(:execute).with("VBoxManage", "list", "runningvms").and_return(mock_running_vms_list_output)
  end
end

module VMHelpers
  def mock_vms_list_output
    "box1 {uuid1-uuid1}\n box2 {uuid2-uuid2}"
  end

  def mock_running_vms_list_output
    "box1 {uuid1-uuid1}"
  end
end
